import React, { useState, useEffect, useRef } from 'react';
import './Chat.css';

function Chat() {
    const [input, setInput] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [isProcessing, setIsProcessing] = useState(false);
    const chatEndRef = useRef(null);

    const handleInputChange = (event) => {
        setInput(event.target.value);
    };

    const handleSend = async () => {
        if (input.trim() === '') return;

        setIsProcessing(true);
        setChatHistory([...chatHistory, { query: input, reply: 'Processing...' }]);

        const response = await fetch('http://localhost:800/api/chat/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message: input }),
        });

        if (response.ok) {
            const data = await response.json();
            setChatHistory((prevHistory) => {
                const newHistory = [...prevHistory];
                newHistory[newHistory.length - 1].reply = data.response;
                return newHistory;
            });
        } else {
            setChatHistory((prevHistory) => {
                const newHistory = [...prevHistory];
                newHistory[newHistory.length - 1].reply = 'Failed to get response';
                return newHistory;
            });
        }

        setInput('');
        setIsProcessing(false);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSend();
        }
    };

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [chatHistory, isProcessing]);

    return (
        <div className="chat-container">
            <div className="chat-history">
                {chatHistory.map((item, index) => (
                    <div key={index} className="chat-message">
                        <div className="user-message"><span className="user-input">{item.query}</span></div>
                        <div className="bot-message">
                            <b className="bot-label">Bot:</b>
                            <span className="bot-response">{item.reply}</span>
                        </div>
                    </div>
                ))}
                <div ref={chatEndRef} />
            </div>
            <div className="input-container">
                <input
                    className="chat-input"
                    value={input}
                    onChange={handleInputChange}
                    onKeyPress={handleKeyPress}
                    placeholder="Type a message..."
                    disabled={isProcessing}
                />
                <button className="chat-button" onClick={handleSend} disabled={isProcessing}>
                    <img src="path/to/send-icon.png" alt="Send" className="send-icon" />
                </button>
            </div>
        </div>
    );
}

export default Chat;
