import React, { useState, useEffect, useRef } from 'react';
import './Chat.css';

function Chat() {
    const [input, setInput] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const chatEndRef = useRef(null);

    const handleInputChange = (event) => {
        setInput(event.target.value);
    };

    const handleSend = async () => {
        if (input.trim() === '') return;

        const response = await fetch('http://localhost:8080/api/chat/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message: input }),
        });

        if (response.ok) {
            const data = await response.json();
            setChatHistory([...chatHistory, { query: input, reply: data.response }]);
            setInput('');
        } else {
            console.error('Failed to send message');
        }
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSend();
        }
    };

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [chatHistory]);

    return (
        <div className="main-container">
            <div className="left-container">
                <div className="chat-history">
                    {chatHistory.map((item, index) => (
                        <div key={index} className="chat-message">
                            <div className="user-message"><b>You:</b> {item.query}</div>
                            <div className="bot-message"><b>Bot:</b> {item.reply}</div>
                        </div>
                    ))}
                    <div ref={chatEndRef} />
                </div>
                <div className="input-container">
                    <input
                        className="chat-input"
                        value={input}
                        onChange={handleInputChange}
                        onKeyPress={handleKeyPress}
                        placeholder="Type a message..."
                    />
                    <button className="chat-button" onClick={handleSend}>Send</button>
                </div>
            </div>
            <div className="right-container">
                <div className="account-info">
                    <img src="path/to/your/logo.png" alt="Account Logo" className="account-logo" />
                </div>
            </div>
        </div>
    );
}

export default Chat;
